mapper.update:
update tbl_student set
    loginPwd=#{loginPwd}
where loginAct=#{loginAct}

mapper.insert:
INSERT INTO tbl_student(
loginAct, name, email, loginPwd, grade, major, department, classId
)VALUES(
#{loginAct}, #{name}, #{email}, #{loginPwd}, #{grade}, #{major}, #{department}, #{classId}
)

mapper.update:
update tbl_student set
loginAct=#{loginAct}, name=#{name}, email=#{email}, grade=#{grade}, major=#{major}, department=#{department}, classId=#{classId}
where id=#{id}

mapper.select:
select
loginAct, name, email, grade, major, department
from tbl_student where id=#{id}
