mapper.update:
update tbl_student set
    loginPwd=#{loginPwd}
where loginAct=#{loginAct}

mapper.insert:
INSERT INTO tbl_student(
loginAct, name, email, loginPwd, grade, major, department, classId
)VALUES(
#{loginAct}, #{name}, #{email}, #{loginPwd}, #{grade}, #{major}, #{department}, #{classId}
)
